import React from 'react';
import styled from 'styled-components';
import { FaFacebook } from 'react-icons/fa';
import { FaHome } from 'react-icons/fa';
import { FaPhone } from 'react-icons/fa';
import { FaEnvelope } from 'react-icons/fa';
import { FaInstagram } from 'react-icons/fa';
import { FaGithub } from 'react-icons/fa';
import { FaYoutube } from 'react-icons/fa';

// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/swiper.min.css";
import "swiper/components/effect-coverflow/effect-coverflow.min.css"
import "swiper/components/pagination/pagination.min.css"

import "./Footer.css";


// import Swiper core and required modules
import SwiperCore, {
  EffectCoverflow,Pagination
} from 'swiper/core';

// install Swiper modules
SwiperCore.use([EffectCoverflow,Pagination]);

function Footer() {
    return (  
        <>
        <Swiper effect={'coverflow'} grabCursor={true} centeredSlides={true} slidesPerView={'auto'} coverflowEffect={{
  "rotate": 50,
  "stretch": 0,
  "depth": 100,
  "modifier": 1,
  "slideShadows": true
}} pagination={true} className="mySwiper">
  <SwiperSlide><img src="https://swiperjs.com/demos/images/nature-1.jpg" /></SwiperSlide><SwiperSlide><img src="https://swiperjs.com/demos/images/nature-2.jpg" /></SwiperSlide><SwiperSlide><img src="https://swiperjs.com/demos/images/nature-3.jpg" /></SwiperSlide><SwiperSlide><img src="https://swiperjs.com/demos/images/nature-4.jpg" /></SwiperSlide><SwiperSlide><img src="https://swiperjs.com/demos/images/nature-5.jpg" /></SwiperSlide><SwiperSlide><img src="https://swiperjs.com/demos/images/nature-6.jpg" /></SwiperSlide><SwiperSlide><img src="https://swiperjs.com/demos/images/nature-7.jpg" /></SwiperSlide><SwiperSlide><img src="https://swiperjs.com/demos/images/nature-8.jpg" /></SwiperSlide><SwiperSlide><img src="https://swiperjs.com/demos/images/nature-9.jpg" /></SwiperSlide>
  </Swiper>
        <FooterCantainer className="main-footer">
            <div className="footer-middle">
                <div className="container">
                    <div className="row">
                        {/* cot 1 */}
                        <div className="col-md-6 ">
                            {/* <img src={Logo} width="15%" alt="logo"/> */}
                            <h4>About Us</h4>
                            <ul className="list-unstyled">
                                <li>Hệ thống của Trung tâm IT được xây dựng tỉ mỉ nhằm nâng cao tính tương tác giữa học viên và giáo viên.
                                    Nó cung cấp kiến thức chân thực và dễ dàng để tiếp thu kiến thức cũng như giúp giáo viên dễ dàng tiếp cận học sinh hơn.</li>
                            </ul>
                        </div>
                        {/* cot 2 */}
                        <div className="col-md-3">
                            <h4>Products</h4>
                            <ul className="list-unstyled">
                                <li><a href="/">React</a></li>
                                <li><a href="/">JavaScript</a></li>
                                <li><a href="/">Programming</a></li>
                                <li><a href="/">Kenvin</a></li>
                            </ul>
                        </div>
                        {/* cot 3 */}
                        <div className="col-md-3">
                            <h4>Contact</h4>
                            <ul className="list-unstyled">
                                <li><FaHome /><a href="/"> Tp.Ho Chi Minh City </a></li>
                                <li><FaPhone /><a href="/"> 0939461842</a></li>
                                <li><FaEnvelope /><a href="/"> ngoctam2303001@gmail.com</a></li>
                            </ul>
                        </div>
                        <hr />
                    </div>
                     <div className="row ml-2 pb-5">
                            <div className="col-md-12">
                            <div className="wrapper">
                            <div className="button">
                                <div className="icon"><i><FaFacebook /></i></div>
                                <span>Facebook</span>
                            </div>
                            <div className="button">
                                <div className="icon"><i><FaInstagram /></i></div>
                                <span>Instagram</span>
                            </div>
                            <div className="button">
                                <div className="icon"><i><FaYoutube /></i></div>
                                <span>Youtube</span>
                            </div>
                            <div className="button">
