import React from 'react';
import { img_500, unavailable } from "../../config/config";
import "./SingleCarousel.css";
// Thông tin từng bộ phim
const SingleContent = ({
    id,
    poster,
    title,
    date,
    media_type,
    vote_average,
}) => {
    return (
        <img
            className="image"
            src={poster ? `${img_500}${poster}` : unavailable}
            alt={title}
        />
    );
};

export default SingleContent;
