import React from 'react';
import "./Header.css";
// import 'bootstrap/dist/css/bootstrap.min.css';
// import { img_300 , unavailable } from "../../config/config";
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';

import images1 from "../../images/images.jpg";
import images2 from "../../images/tải xuống.jpg";
import images3 from "../../images/thuat-ngu-dien-anh-02-33c96a.jpg";

const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 5
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 3
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1
  }
};

const Header = ({
  title,
}) => {
  return (
    <span onClick={() => window.scroll(0, 0)} className="header">
      <Carousel responsive={responsive}>
      <div className="container">
        <div className="page">
          <div className="row">
            <div className="col-md-9">
              <div className="slider">
                <ul className="sliders">
                  <li><a href="#"><img src={images1} alt={title}></img></a></li>
                  <li><a href="#"><img src={images2} alt={title}></img></a></li>
                  <li><a href="#"><img src={images3} alt={title}></img></a></li>
                </ul>
              </div>
            </div>
            <div className="col-md-3">
              <div className="row">
                <div className="col-md-6 col-md-12">
                  <div className="latest-movie">
                    <a href="#"><img src="" alt=""></img></a>
                  </div>

                </div>
                <div className="col-md-6 col-md-12">
                  <div className="latest-movie">
                    <a href="#"><img src="" alt=""></img></a>
                  </div>

                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      </Carousel>
    </span>

  );
};

export default Header;
